INSERT INTO `products` (`id`, `name_product`, `price`, `description`, `status`, `id_category`, `sold`, `discount`, `createdAt`, `updatedAt`) VALUES
(1, 'Sò điệp nướng mỡ hành', 80000, 'Sò điệp là loại hải sản có phần thịt cùi trắng ngà, thơm ngon, vị ngọt, tính mát, không dai, không độc và rất tốt cho sức khỏe. Nhâm nhi món này, bạn sẽ cảm nhận được vị ngọt dai của sò điệp, mùi thơm của hành lá, beo béo của tóp mỡ, bùi bùi của đậu phộng hòa quyện cùng nước mắm chua chua ngọt ngọt.', 3, 3, 5, 0, '2023-10-28 18:41:18', '2023-10-31 13:57:36'),
(2, 'Mì xào hải sản', 70000, 'Mì xào hải sản là món ăn quen thuộc dễ ăn được nhiều người yêu thích, sợi mì giòn dai thấm gia vị kèm với hải sản tươi ngon, ăn kèm nước tương có vài lát ớt. Màu sắc bắt mắt và mùi thơm hấp dẫn từ hải sản sẽ kích thích vị giác ngay tức khắc.\r\n', 0, 4, 6, 0, '2023-10-28 18:47:06', '2023-10-31 13:47:11'),
(3, 'Ghẹ sốt chua cay', 250000, 'Ghẹ là món ăn rất giàu chất dinh dưỡng, nhất là với phụ nữ mang thai và trẻ nhỏ. Với món ghẹ sốt chua cay bạn hoàn toàn có thể ăn với miến trộn cùng nước sốt cay hay cơm trắng tuỳ ý bạn nhé.Vì ghẹ đã đẫm nước sốt, nên bạn không cần thiết phải chấm với gia vị hay nước sốt hải sản. Với món ăn này, bạn hãy ăn ngay khi còn nóng để cảm nhận vị ngọt từ ghẹ nhé.', 0, 4, 3, 0, '2023-10-28 18:51:16', '2023-10-31 11:53:34'),
(4, 'Mực nhồi nướng sốt chua cay', 180000, 'Mực nhồi thịt nướng có màu vàng nâu óng ánh, mùi hương nướng bếp thơm lừng của của mực và thịt. Miếng mực bên ngoài dai sần sật, thịt bên trong mềm bùi đậm vị ăn cùng nước chấm pha rất tuyệt vời!', 0, 3, 0, 0, '2023-10-28 18:54:26', '2023-10-28 18:54:26'),
(5, 'Tôm sốt Thái', 200000, 'Nếu là một người “cuồng” các món Thái, chắc hẳn bạn không còn lạ gì với món tôm sống chấm sốt ớt cay. Vị cay tê tái của sốt Thái kết hợp với vị ngọt của tôm mà không hề tanh.', 0, 4, 0, 0, '2023-10-28 18:57:31', '2023-10-28 18:57:31'),
(6, 'Ốc hương sốt trứng muối', 180000, 'Món ốc hương trứng muối gây nghiện bởi sự kết hợp đầy tinh tế giữa thịt ốc hương ngon ngọt cùng vị bùi béo của trứng muối. Màu vàng óng ánh của trứng muối sẽ tô điểm thêm và tăng sự hấp dẫn của món ốc hương vốn đã siêu “bắt mồi”. ', 3, 4, 0, 0, '2023-10-28 19:01:12', '2023-10-30 18:40:05'),
(7, 'Tôm nướng muối ớt', 120000, 'Một chút cay nồng của ớt, một chút dai ngon của thịt tôm, vị mặn đậm đà của muối, tất cả hòa quyện và được nướng trên bếp than hồng thơm phức luôn luôn là món ăn níu kéo vị giác của bạn.', 3, 3, 0, 0, '2023-10-28 19:09:57', '2023-10-30 18:40:05'),
(8, 'Tôm chiên xù', 140000, 'Món tôm chiên xù có lớp vỏ ngoài vàng ươm, giòn rụm. Bên trong là thịt tôm tươi ngon, dai ngọt vô cùng hấp dẫn.\r\n', 0, 2, 0, 0, '2023-10-28 19:12:42', '2023-10-28 19:12:42'),
(9, 'Cua rang me', 270000, ' Với chút giòn tan, thơm lừng, beo béo của vị cua rang kết hợp với vị chua chua, ngọt ngọt của nước sốt me đã tạo nên món ăn vô cùng hấp dẫn trong những ngày nắng hè.\r\n', 0, 4, 0, 0, '2023-10-28 19:20:48', '2023-10-28 19:20:48'),
(10, 'Lẩu cá bớp', 400000, 'Cá bớp là loại cá biển chứa nhiều dưỡng chất như Omega, Canxi,... đặc biệt là Protein và DHA giúp phòng chống xơ vữa động mạch, cao huyết áp, nhồi máu cơ tim,.... Thịt cá bớp có màu trắng, dai nhưng vẫn mềm, chắc và ngọt. Các món lẩu được làm từ cá bớp luôn có chứa hàm lượng dinh dưỡng cao giúp bồi bổ sức khỏe khá tốt. Do đó, chỉ cần một lần bạn được thưởng thức qua loại cá này thì chắc chắn sẽ không bao giờ quên được hương vị của nó.', 0, 1, 0, 0, '2023-10-28 19:22:16', '2023-10-28 19:22:16'),
(11, 'Nghêu hấp xả', 70000, 'Đối với các tín đồ mê hải sản thì món nghêu hấp sả Thái chắc chắn là một món kinh điển. Nghêu béo ngậy với vị mặn ngọt hòa quyện của vị chua chua của me, vị cay nồng của sả và ớt sẽ khiến bạn xiêu lòng', 3, 5, 0, 0, '2023-10-28 19:24:21', '2023-10-30 18:40:05'),
(12, 'Hàu ngũ vị', 130000, 'Với các loại sốt khác nhau như tỏi, tiêu xanh, phô mai, thái, trứng cút... hàu nướng cùng công thức đặc biệt sẽ giúp bạn tận hưởng vị thịt hàu dai thơm phủ lên trên bề mặt là những loại nước sốt không thể chối từ.', 0, 5, 0, 0, '2023-10-28 19:25:18', '2023-10-28 19:25:18'),
(13, 'Bạch tuộc nướng sa tế', 220000, 'Bạch tuộc nướng sa tế luôn là món ăn giòn rụm, cay ngon được lòng tất cả mọi người kể cả người khó tính nhất. Bạch tuộc rắc thêm xíu rau mùi hoà quyện cùng với muối tiêu chanh giúp tăng thêm hương vị thơm ngon.', 3, 3, 0, 0, '2023-10-28 19:26:40', '2023-10-30 18:40:05'),
(14, 'Cá cơm chiên giòn', 60000, 'Tận hưởng hương vị beo béo, giòn tan mằn mặn kết hợp với vị cay cay của ớt và tỏi của món cá cơm chiên giòn chắc chắn bạn sẽ không bao giờ cảm thấy chán. Cá cơm chiên giòn là sản phẩm chế biến sẵn với thành phần chính là cá cơm khô đã được tẩm ướp gia vị vừa miệng.', 3, 2, 0, 0, '2023-10-28 19:28:48', '2023-10-30 18:40:05'),
(15, 'Cá lem nướng ', 90000, 'Cá lem nướng bằng bếp than khi chín có màu vàng nâu hấp dẫn kết hợp với mùi thơm đặc trưng của mắc khén, kích thích vị giác và sự ngon miệng khi thưởng thức. Cá vẫn giữ được vị ngọt thanh, thịt cá mềm và đậm đà cực kì. Phần da cá giòn dai, cực kì ngon miệng và hấp dẫn.', 3, 3, 0, 0, '2023-10-28 19:29:30', '2023-10-30 18:40:05'),
(16, 'Tôm hùm sốt bơ tỏi', 900000, 'Tôm hùm sốt bơ tỏi có màu đỏ bắt mắt, từng thớ thịt tôm chắc ngọt, hương vị đậm đà hòa quyện cùng mùi thơm, vị béo ngậy của bơ tỏi.Tôm hùm sốt  bơ tỏi sẽ là món ăn cực kì sang trọng, bắt mắt và không kém phần ngon miệng.', 0, 4, 0, 0, '2023-10-28 19:30:59', '2023-10-28 19:30:59'),
(17, 'Cơm chiên hải sản', 70000, 'Sự kết hợp của cơm và các loại hải sản tươi ngon cùng phương pháp chiên/rang sẽ cho ra một món ăn vô cùng hấp dẫn. Từng hạt cơm dẻo thơm thấm đều gia vị khiến bạn vô cùng thích thú. Hạt cơm tơi, thấm vị, vàng óng bắt mắt.', 0, 2, 0, 0, '2023-10-30 16:50:11', '2023-10-30 16:50:25'),
(18, 'Gỏi sứa', 180000, 'Nộm sứa hay còn gọi là gỏi sứa bắt mắt với nhiều màu sắc hấp dẫn từ các loại rau củ. Sứa thì dai dai, giòn giòn, cà rốt, hành tây, dưa leo thì tươi, ngọt nước, thanh mát. Tất cả hòa quyện đều cùng nước mắm chua ngọt, mằn mặn, cay cay, thêm chút bùi béo, thơm thơm của đậu phộng. ', 3, 6, 0, 0, '2023-10-30 16:56:19', '2023-10-30 18:40:05'),
(19, 'Ốc móng tay xào bơ tỏi', 140000, 'Ốc móng tay xào bơ tỏi với hương vị đậm đà, ngọt cay béo ngậy quện vào thịt ốc dai dai giòn giòn chắc chắn khiến người dung thổn thức không thôi.', 0, 4, 0, 0, '2023-10-30 17:14:03', '2023-10-30 17:14:14'),
(20, 'Vẹm xanh nướng phô mai', 16000, 'Vẹm xanh nướng phô mai mang lại hương vị tuyệt vời bởi vị ngọt đặc trưng và bùi béo hấp dẫn. Là món ăn đầy đặn, kết cấu tinh tế, mềm mại và có hương vị đậm đà, ngon ngọt, đồng thời rất giàu đạm, vitamin và khoáng chất.', 0, 3, 0, 0, '2023-10-30 17:14:49', '2023-10-30 17:15:05'),
(21, 'Salad Tôm', 70000, 'Salad tôm với thành phần chính là tôm, kết hợp cùng nhiều nguyên liệu rau củ quả khác. Tôm từ lâu đã được biết đến là thực phẩm chứa nhiều thành phần dinh dưỡng tốt cho cơ thể. Cụ thể, trong tôm có protein, khoáng chất...Món salad tôm thường được kết hợp bằng những nguyên liệu rau củ quả chứa nhiều chất xơ nên xứng đáng là món ăn giảm cân, thanh mát cho ngày hè. \n', 0, 6, 0, 0, '2023-10-30 17:18:45', '2023-10-30 17:18:57'),
(22, 'Mực xào sa tế', 200000, 'Mực xào sa tế có màu sắc hấp dẫn, mực tươi giòn thơm sần sật, cay cay vừa ăn kết hợp cùng hành tây ngọt thanh.', 3, 4, 0, 0, '2023-10-30 17:25:15', '2023-10-30 18:40:05'),
(23, 'Râu mực nướng muối ớt', 170000, 'Râu mực tươi với phần thịt có độ dai, ngọt, thơm và giòn thường được sử dụng chế biến thành nhiều món ăn như râu mực nướng, mực nhồi thịt, salad mực, canh mực,... ', 3, 3, 0, 0, '2023-10-30 17:27:05', '2023-10-30 18:40:05'),
(24, 'Mực chiên xù', 200000, 'Mực có vị ngọt tự nhiên, dai dai và được phủ lớp bột giòn rụm bên ngoài tạo thành một món ăn rất ngon khiến cho từ người lớn đến trẻ nhỏ đều thích thú.', 0, 2, 0, 0, '2023-10-30 17:28:33', '2023-10-30 17:28:56'),
(25, 'Cua hoàng đế', 1600000, 'Cua hoàng đế hấp nước dừa là một sự kết hợp thú vị mang đến hương vị mới lạ nhưng vô cùng hoàn hảo.  Vị ngon ngọt, béo ngậy của thịt cua hoàng đế quyện cùng hương thơm thanh ngọt cùng vị béo của nước dừa mang đến một món ăn hấp dẫn, không thể bỏ qua.', 0, 5, 0, 0, '2023-10-30 17:31:07', '2023-10-30 17:32:18'),
(26, 'Cá đuối nấu mẻ', 350000, 'Cá đuối là một loại cá biển có nhiều xương sụn, nên ăn rất giòn. Thịt cá đuối nổi tiếng ngọt và dai.Món lẩu đơn giản, không quá nhiều nguyên liệu nhưng dễ thu hút thực khách bởi những miếng cá ngọt giòn được tẩm ướp gia vị phù hợp.', 0, 1, 0, 0, '2023-10-30 17:34:16', '2023-10-30 17:35:25'),
(27, 'Ốc mỡ nướng tiêu xanh', 150000, 'Thịt ốc giòn dai quyện với vị the cay từ hạt tiêu xanh cùng vị mặn ngọt chua hài hòa, kích thích vị giác. Món này ngon nhất khi ăn nóng và kèm các loại rau như: Rau răm, lá xoài non, lá lốt... ', 0, 3, 0, 0, '2023-10-30 17:36:54', '2023-10-30 17:37:30'),
(28, 'Lẩu cua', 400000, 'Nước lẩu cua đậm đà với riêu cua béo ngậy, chả cá thác lác dai, mềm ăn kèm với rau và bún tươi thì ngon quên lối về.', 0, 1, 0, 0, '2023-10-30 17:41:55', '2023-10-30 17:42:38'),
(29, 'Lẩu thái', 400000, 'Nước lẩu chua ngọt nhẹ từ cam và thơm, thơm mùi củ riềng, vị ngọt của tôm, mực và ba chỉ bò ăn kèm một ít măng chua và rau, thì thật là tuyệt vời.', 0, 1, 0, 0, '2023-10-30 17:44:39', '2023-10-30 17:45:27'),
(30, 'Gỏi tôm thịt', 250000, 'Đây là món ăn thanh đạm, tươi mát gây kích thích vị giác người thưởng thức. Với độ mềm ngọt của tôm thịt, kết hợp với gỏi từ các loại rau củ tốt cho sức khỏe, đây là sự lựa chọn tuyệt vời dành cho người thích lành mạnh.', 0, 6, 0, 0, '2023-10-30 17:47:52', '2023-10-30 17:48:28'),
